    // private static final String
    @Override
    public void validate(Client client) {
        Pattern patternEmail = Pattern.compile(EMAIL_PATTERN);
        if (!patternEmail.matcher(client.getClientEmail()).matches()) {
            throw new IllegalArgumentException("Email is not a valid email!");
        }

        Pattern patternName =Pattern.compile(NAME_PATTERN);
        if(!patternName.matcher(client.getClientName()).matches()){
            throw new IllegalArgumentException("Name is not a valid name!");
        }

        Pattern patternId = Pattern.compile(ID_PATTERN);
        if(!patternId.matcher(String.valueOf(client.getClientId())).matches()){
            throw new IllegalArgumentException("Id is not a valid id!");
        }
    }
